mapper.update:
UPDATE MEMBER
SET 
	ICON_NO = #{iconNo}
WHERE NO = #{memberNo}

  	UPDATE MEMBER
	SET POINT = POINT - (
	    SELECT PRICE
	    FROM ICON
	    WHERE NO = #{iconNo}
	    ),
	    ACCUMULATE = ACCUMULATE + (
	    SELECT PRICE
	    FROM ICON
	    WHERE NO = #{iconNo}
	    )
	WHERE NO = #{memberNo}
	AND POINT >= (
	    SELECT PRICE
	    FROM ICON
	    WHERE NO = #{iconNo}
	)

mapper.insert:
  	INSERT INTO MEMBER_ICON (
	    MEMBER_NO
	    , ICON_NO)
	VALUES (
	    #{memberNo}
	    , #{iconNo}
	)

INSERT INTO ICON (
	NO
	, TITLE
	, PRICE
	, PHOTO
)
VALUES (
	SEQ_ICON_NO.NEXTVAL
	, #{title}
	, #{price}
	, #{photo}
)
