mapper.insert:
INSERT INTO MEMBER (
    NO
    , MEMBER_TITLE_NO
    , NICK
    , ICON_NO
    , ID
    , PWD
    , EMAIL
    , POINT
    ) 
VALUES (
    SEQ_MEMBER_NO.NEXTVAL
    , 1	
    , #{nick}
    , 1
    , #{id}
    , #{pwd}
    , #{email}
    , 500
    )

mapper.select:
SELECT M.NO, M.MEMBER_TITLE_NO, MT.TITLE AS MEMBER_TITLE, M.ICON_NO, M.ID, M.PWD, M.NICK, M.EMAIL, M.POINT, M.ENROLL_DATE, M.DEL_YN, i.PHOTO FROM MEMBER M JOIN ICON i ON M.ICON_NO = i.NO LEFT JOIN MEMBER_TITLE MT ON M.MEMBER_TITLE_NO = MT.NO WHERE M.DEL_YN = 'N'
AND M.ID = #{id} AND M.PWD = #{pwd}
AND M.NO = #{no}

mapper.update:
UPDATE MEMBER SET
	NICK = #{nick},
	PWD = #{pwd}
WHERE NO = #{no}

mapper.select:
SELECT COUNT(*) AS count FROM MEMBER WHERE DEL_YN = 'N'
AND ID = #{id}
AND NICK = #{nick}

SELECT COUNT(NO) AS CNT
FROM POINT
WHERE MEMBER_NO = #{memberNo}
