mapper.select:
SELECT Q.NO, Q.QUE_CATE_NO , Q.LOC_CATE_NO , Q.TITLE, Q.CONTENT, Q.POINT, TO_CHAR(Q.ENROLL_DATE, 'YYYY/MM/DD HH24:MI') AS ENROLL_DATE, Q.IMG_PATH, Q.LATITUDE, Q.LONGITUDE, Q.DEL_YN, Q.HEAD_CNT, ROUND(AVG(CQ.RATE), 2) AS RATING
FROM QUEST Q LEFT JOIN COM_QUEST CQ ON Q.NO = CQ.QUEST_NO WHERE Q.DEL_YN = 'N'
AND Q.LOC_CATE_NO = #{locCateNo}
AND Q.TITLE = #{title}
AND Q.POINT >= #{point}
AND Q.HEAD_CNT = #{headCnt}
AND QUE_CATE_NO = #{queCateNo}
GROUP BY Q.NO, Q.QUE_CATE_NO ,Q.LOC_CATE_NO , Q.TITLE, Q.CONTENT, Q.POINT, Q.ENROLL_DATE, Q.IMG_PATH, Q.LATITUDE, Q.LONGITUDE, Q.HEAD_CNT,Q.DEL_YN ORDER BY RATING DESC

SELECT Q.NO, Q.QUE_CATE_NO , QC.NAME AS CATEGORY_NAME, Q.LOC_CATE_NO , Q.TITLE, Q.CONTENT, Q.POINT, TO_CHAR(Q.ENROLL_DATE, 'YYYY/MM/DD HH24:MI') AS ENROLL_DATE, Q.IMG_PATH, Q.LATITUDE, Q.LONGITUDE, Q.DEL_YN, Q.HEAD_CNT, ROUND(AVG(CQ.RATE), 2) AS RATING FROM QUEST Q LEFT JOIN COM_QUEST CQ ON Q.NO = CQ.QUEST_NO LEFT JOIN QUEST_CATEGORY QC ON Q.QUE_CATE_NO = QC.NO WHERE Q.DEL_YN = 'N'
AND Q.LOC_CATE_NO = #{locCateNo}
AND Q.TITLE = #{title}
AND Q.POINT >= #{point}
AND Q.HEAD_CNT = #{headCnt}
AND QUE_CATE_NO = #{queCateNo}
GROUP BY Q.NO, Q.QUE_CATE_NO, QC.NAME, Q.LOC_CATE_NO, Q.TITLE, Q.CONTENT, Q.POINT, Q.ENROLL_DATE, Q.IMG_PATH, Q.LATITUDE, Q.LONGITUDE, Q.HEAD_CNT, Q.DEL_YN ORDER BY CASE WHEN RATING IS NULL THEN 1 ELSE 0 END, -- NULL인 경우에는 맨 아래로 RATING DESC OFFSET #{pageNo} ROWS FETCH NEXT #{limit} ROWS ONLY

mapper.insert:
INSERT INTO QUEST
(
    NO
	,QUE_CATE_NO
	,LOC_CATE_NO
	,TITLE
	,CONTENT
	,POINT
	,IMG_PATH
	,LATITUDE
	,LONGITUDE
	,HEAD_CNT
)
VALUES
(
    SEQ_QUEST_NO.NEXTVAL
    ,#{queCateNo}
	,#{locCateNo}
	,#{title}
	,#{content}
	,#{point}
	,#{imgPath}
	,#{latitude}
	,#{longitude}
	,#{headCnt}
)

INSERT INTO COM_QUEST (
	NO
    , MEMBER_NO
    , QUEST_NO
    , RATE
    , IMG_PATH
    )
VALUES
    (
    SEQ_COM_QUEST_NO.NEXTVAL
    , #{memberNo}
    , #{questNo}
    , #{rate}
    , #{imgPath}
    )

mapper.select:
	SELECT NVL(STATE, 0) AS RESULT_STATE
    FROM COM_QUEST
    WHERE MEMBER_NO = #{memberNo} AND QUEST_NO = #{questNo}
