mapper.insert:
INSERT INTO COMMUNITY_BOARD
(
  NO,
  MEMBER_NO,
  TITLE,
  CONTENT,
  ENROLL_DATE
)
VALUES
(
  SEQ_COMMUNITY_BOARD_NO.NEXTVAL
  , #{memberNo}
  , #{title}
  , #{content}
  , SYSDATE
)

mapper.select:
SELECT COUNT(*) 
FROM community_like 
WHERE MEMBER_NO = #{memberNo} AND COMMUNITY_NO = #{boardNo}

mapper.insert:
INSERT INTO COMMUNITY_LIKE 
(
	MEMBER_NO, 
	COMMUNITY_NO
)
VALUES 
(
	#{memberNo}, 
	#{boardNo}
)

mapper.delete:
Delete
	FROM COMMUNITY_LIKE
	WHERE MEMBER_NO = #{memberNo} AND COMMUNITY_NO = #{boardNo}
