mapper.select:
SELECT COUNT(NO) AS CNT
FROM COMMT
WHERE DEL_YN = 'N' AND BOARD_NO = #{boardNo}

SELECT COUNT(*) 
FROM COMMT_LIKE 
WHERE MEMBER_NO = #{memberNo} AND COMMT_NO = #{no}

mapper.insert:
INSERT INTO COMMT_LIKE 
(
	MEMBER_NO, 
	COMMT_NO
)
VALUES 
(
	#{memberNo}, 
	#{no}
)

mapper.delete:
Delete
	FROM COMMT_LIKE
	WHERE MEMBER_NO = #{memberNo} AND COMMT_NO = #{no}
