mapper.select:
SELECT NO, ADMIN_NO, TITLE, CONTENT, ENROLL_DATE, DEL_YN FROM NOTICE_BOARD WHERE DEL_YN = 'N'
AND TITLE LIKE #{pattern}
AND CONTENT LIKE #{pattern}

SELECT NB.NO, NB.ADMIN_NO, NB.TITLE, NB.CONTENT, NB.ENROLL_DATE, NB.DEL_YN, A.ID FROM NOTICE_BOARD NB JOIN ADMIN A ON A.NO = NB.ADMIN_NO WHERE DEL_YN = 'N'
AND NB.TITLE LIKE #{pattern}
AND NB.CONTENT LIKE #{pattern}
OFFSET #{pageNo} ROWS FETCH NEXT #{limit} ROWS ONLY

mapper.insert:
  	INSERT INTO NOTICE_BOARD
(
    NO, 
    TITLE,
    CONTENT,
)
VALUES
(
    SEQ_NOTICE_BOARD_NO.NEXTVAL
    ,#{title}
    ,#{content}
);
